function loadOS(e){document.querySelectorAll(".mac").forEach(function(n){n.style.display="mac"===e?"":"none"}),document.querySelectorAll(".win").forEach(function(n){n.style.display="win"===e?"":"none"}),document.querySelectorAll(".linux").forEach(function(n){n.style.display="linux"===e?"":"none"})}document.addEventListener("DOMContentLoaded",function(){-1<navigator.platform.toUpperCase().indexOf("WIN")?loadOS("win"):-1<navigator.platform.toUpperCase().indexOf("LINUX")?loadOS("linux"):loadOS("mac")});